import React, { Component } from 'react'

class SearchBar extends Component {
    constructor(props) {
        super(props)

        this.state = {
            bookList: [],
            search: ''
        }
    }
    componentDidMount = () => {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response => response.json())
            .then(json => this.setState({ bookList: json }))
    }
    handleChange = (e) => {
        e.preventDefault();
        this.setState({ search: e.target.value })
    }
    render() {
        let { bookList, search } = this.state;
        return (
            <div>
                <form>
                    <input onChange={this.handleChange} value={search} autoComplete="off" />
                </form>
                {search}
                {bookList.map(book => <div key={book.id}>{book.name}</div>)}
            </div>
        )
    }
}

export default SearchBar;
