import React, { Component } from 'react'

class SearchBar extends Component {
    constructor(props) {
        super(props)

        this.state = {
            bookList: [],
            search: ''
        }
    }
    componentDidMount = () => {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response => response.json())
            .then(json => this.setState({ bookList: json }))
    }
    handleChange = (e) => {
        this.setState({ e })
    }
    render() {
        let { bookList, input } = this.state;
        return (
            <div>
                <form>
                    <input type="text" id="search" onChange={this.handleChange} value={input} autoComplete="off" />
                </form>
                {input}
                {bookList.map(book => <div key={book.id}>{book.name}</div>)}
            </div>
        )
    }
}

export default SearchBar;
