import React, { Component } from 'react'
import { DropdownButton, Dropdown, ButtonGroup } from 'react-bootstrap';
import { BrowserRouter as Router, Link } from 'react-router-dom';
class GenreList extends Component {
    constructor(props) {
        super(props)

        this.state = {
            bookList: [],
            search: ''
        }
    }
    componentDidMount = () => {
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response => response.json())
            .then(json => this.setState({ bookList: json }))
    }
    handleChange = (e) => {
        this.props.history.push(`${e.target.value}`);
    }
    render() {
        return (
            <div>
                <div>
                    <div>
                        {[DropdownButton].map((DropdownType, idx) => (
                            <>
                                <DropdownType
                                    as={ButtonGroup}
                                    key={idx}
                                    id={`dropdown-button-drop-${idx}`}
                                    size="sm"
                                    variant="warning"
                                    title="Drop small"
                                    onClick={this.handleChange}
                                >
                                    <Dropdown.Item value="Action">Action</Dropdown.Item>
                                    <Dropdown.Item value="Novel">Novel</Dropdown.Item>
                                    <Dropdown.Item value="Fantasy">Fantasy</Dropdown.Item>
                                    <Dropdown.Item value="Non-fiction">Non-fiction</Dropdown.Item>

                                </DropdownType>
                            </>
                        ))}
                    </div>
                </div>
            </div>
        )
    }
}

export default GenreList;
