import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import './login-page.styles.scss';

import firebase from "../../assets/Firebase/firebase";
import "firebase/auth";

import { TextField, Button } from '@material-ui/core';
import { UserContext } from '../provider/user.provider';

function LoginPage() {
    const [inputValues, setInputValues] = useState({
        username: '', password: ''
    });
    handleChange = event => {
        const { name, value } = event.target;
        setInputValues({ ...inputValues, [name]: value });
    };

    return (
        <div className='container-form'>
            <TextField
                autoComplete="off"
                label="Email"
                name="email"
                type='email'
                onChange={this.handleChange}
                value={email}
            />
            <TextField
                label="Password"
                name="password"
                type='password'
                onChange={this.handleChange}
                value={password}
            />
            <small><Link to="/register">New User?</Link></small>
            <Button type='submit' variant="contained" color="primary" onClick={this.handleClick}>Submit</Button>
            <Button type='submit' variant="contained" color="secondary" onClick={this.handleClick}>Sign in with Google</Button>
        </div>
    )
}
handleClick = () => {
    let user;
    let token;
    const auth = firebase.auth();
    const provider = new firebase.auth.GoogleAuthProvider();
    provider.setCustomParameters({ prompt: 'select_account' });
    auth.signInWithPopup(provider).then(function (result) {
        // This gives you a Google Access Token. You can use it to access the Google API.
        token = result.credential.accessToken;
        // The signed-in user info.
        user = result.user;
    }).then(this.updateUser(user));
}
updateUser = (user) => {
    const [setUser] = useContext(UserContext);
    setUser(user);
}


export default LoginPage;
