import React, { Component } from 'react';
import books from './directory.styles.scss';
import '../../assets/database';

import DirectoryItem from '../directory-item/directory-item.component';
class Directory extends Component {
    constructor(props) {
        super(props)

        this.state = {
            ListOfGenres: [],
            list: ""
        }
    }
    componentDidMount() {
        let list = books.map(genre =>
            <div className="genre-list-container">
                <div className="genre-list-title">{genre.genreName}</div>
                <div className="genre-items">
                    {
                        genre.bookList.filter((items, idx) => idx < 2).map(item => (
                            <div>
                                <DirectoryItem bookName={item.bookName} bookImage={item.bookImage} bookAuthor={item.bookAuthor} />
                            </div>
                        )

                        )
                    }
                </div>
            </div>
        )
        this.setState({ list })
    }
    render() {
        let { list } = this.state;
        return (
            list
        )
    }
}

export default Directory;
