import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import './login-page.styles.scss';
import { signInWithGoogle } from "../../assets/Firebase/firebase";
import { auth } from '../../assets/Firebase/firebase';

import { TextField, Button } from '@material-ui/core';

class LoginPage extends Component {
    constructor(props) {
        super(props)

        this.state = {
            email: "",
            password: "",
            currentUser: []

        }
    }
    handleChange = (e) => {
        this.setState({ [e.target.name]: e.target.value })
    }
    handleAuth = () => {
        auth.then(function (result) {
            // This gives you a Google Access Token. You can use it to access the Google API.
            var token = result.credential.accessToken;
            console.log("Token =  " + token);
            // The signed-in user info.
            var user = result.user;
            console.log("User =  " + user);
            // ...
        }).catch(function (error) {
            // Handle Errors here.
            var errorCode = error.code;
            var errorMessage = error.message;
            // The email of the user's account used.
            var email = error.email;
            // The firebase.auth.AuthCredential type that was used.
            var credential = error.credential;
            // ...
        });
    }
    render() {
        let { email, password } = this.state;
        return (
            <div className='container-form'>
                <TextField
                    autoComplete="off"
                    label="Email"
                    name="email"
                    type='email'
                    onChange={this.handleChange}
                    value={email}
                />
                <TextField
                    label="Password"
                    name="password"
                    type='password'
                    onChange={this.handleChange}
                    value={password}
                />
                <small><Link to="/register">New User?</Link></small>
                <Button type='submit' variant="contained" color="primary" onClick={this.handleClick}>Submit</Button>
                <Button type='submit' variant="contained" color="secondary" onClick={this.handleAuth}>Sign in with Google</Button>
            </div>
        )
    }
}

export default LoginPage;
